    /**
     * 把一个类别的原始str转为map类型
     * @param originStr
     * @param index
     * @return
     */
    private Map<String,Integer> getMapper(String originStr,Integer index){
        Map<String,Integer> map=new HashMap<>();
        originStr=toTraditional(originStr);
        String[] items = originStr.split("\\(([\\u4E00-\\u9FA5]+)\\)\\s");
        for(String item:items){
            map.put(item,index);
        }
        return map;
    }

    /**
     * 转str为繁体
     * @param str
     * @return
     */
    private String toTraditional(String str){
        return converter.convert(str);
    }

    /**
     * 准备全部类别的map
     * @return
     */
    public Map<String,Integer> toMapper(){
        DataUtil pc=new DataUtil();
        for(int i=117;i<=174;i++){
            String itemName="category_"+i;
            try {
                Field field=DataUtil.class.getField(itemName);
                String item=(String)field.get(pc);
                map.putAll(getMapper(item,i));
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
        return map;
    }



